<script>
import { Card, Button, FormGroupInput } from '@/components';
import axios from 'axios'
import MainFooter from '@/layout/MainFooter';
import bootstrap from 'bootstrap'
export default {
  name: 'login-page',
  bodyClass: 'login-page',
  data(){
        return{
            search:'',
            dialog: false,
            embarcaciones:[],
            agencias:[],
            companias:[],
            confirmarServicio:[],
            tipoServicios:['Terrestre','Cabotaje'],
            headers: [
                { text: 'Detalles', value: 'action', sortable: false },
                { text: 'CompaÃ±ia', value: 'cia.cia' },
                { text: 'Embarcacion', value: 'embarcacion.nombre' },
                { text: 'agencia', value: 'agencia.agencia' },
                { text: 'Estado', value: 'estado' },
            ],
            nombre:'',
            editedIndex: -1,
            _id:'',
            id:'',
            cia:'',
            embarcacion:'',
            muelle:'',
            agencia:'',
            onhire:'',
            usuario:'',
            programa:'',
            detalles:'',
            tipoServicio:'',
            fecha:'',
            agencia:'',
            urlSoporte:'',
            urlCotizacion:'',
            adAccion:0,
            adNombre:'',
            adModal:'',
            adId:'',
            comprobanteModal:0
       
        }
    },
  components: {
    Card,
    MainFooter,
    [Button.name]: Button,
    [FormGroupInput.name]: FormGroupInput
  },

   computed:{
     formTitle () {
      return this.editedIndex === -1 ? 'Nueva EmbarcaciÃ³n' : 'Editar EmbarcaciÃ³n'
    },
    },
    
  watch: {
    dialog (val) {
      val || this.close()
    },
  },
    created () {
       this.listar()
    this.selectEmbarcaciones()
    this.selectAgencias()
    this.selectCia()
    },
   methods:{
 listar(){
         let header={"Token":this.$store.state.token};
          let configuracion= {headers: header}
          let me=this
          axios.get('confirmarServicio/listServicio',configuracion).then(function (response){
              me.confirmarServicio=response.data
          }).catch(function(error){
              console.log(error)
          })
      },
      limpiar(){
          this._id='';
          this.nombre='',
          this.tipo='',
          this.armador='',
          this.eslora='',
          this.manga='',
          this.tbr='',
          this.trb='',
          this.calado=''
          this.editedIndex=-1
      },
      guardar(){
          let me = this;
            let header={"Token":this.$store.state.token};
          let configuracion= {headers: header}
          if(this.editedIndex >-1){
               axios.put('confirmarServicio/update',{'_id':this._id,'cia':this.cia,'embarcacion':this.embarcacion,'agencia':this.agencia,
              'onhire':this.onhire,'programa':this.programa,'detalles':this.detalles,'tipo_servicio':this.tipoServicio,'url_soporte':this.urlSoporte,'url_cotizacion':this.urlCotizacion},configuracion)
              .then(function(response){
                  me.limpiar();
                  me.close();
                  me.listar();
              })
              .catch(function(error){
                  console.log(error);
              })
          }else{
             axios.post('confirmarServicio/add',
             {
             'cia':this.cia,
             'embarcacion':this.embarcacion,
             'agencia':this.agencia,
             'onhire':this.onhire,
             'programa':this.programa,
             'usuario':this.$store.state.usuario._id,
             'detalles':this.detalles,
             'tipo_servicio':this.tipoServicio,
             'url_cotizacion':this.urlCotizacion,
             'url_soporte':this.urlSoporte
              },configuracion)
              .then(function(response){
                  me.limpiar();
                  me.close();
                  me.listar();
              })
              .catch(function(error){
                  console.log(error);
              })
          }
      },
    initialize () {
     
    },

      crearPDF(){
            var quotes = document.getElementById('servicio')
             html2canvas(quotes).then(function (canvas) {
                    var imgData = canvas.toDataURL('image/png');
                    var imgWidth = 210;
                    var pageHeight = 295;

                    var imgHeight = canvas.height * imgWidth / canvas.width;
                    var heightLeft = imgHeight;
                    
                    var doc = new jsPDF('p', 'mm', 'a4');
                    var position = 0;

                    doc.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
                    heightLeft -= pageHeight;

                    while (heightLeft >= 0) {
                        position = heightLeft - imgHeight;
                        doc.addPage();
                        doc.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
                        heightLeft -= pageHeight;
                    }
                    doc.save('solicitudUA.pdf');
            })
          },

           mostrarComprobante(item){
            
            var options = { year: 'numeric', month: 'long', day: 'numeric' };
            var fec = new Date();   
            this.fecha = fec.toLocaleString(); 
            this.agencia = item.agencia
            this.embarcacion = item.embarcacion
            this.programa = item.programa
            this.detalles = item.detalles
            this.tipo_servicio=item.tipo_servicio
            this.onhire=item.onhire
            this.muelle=item.muelle
            this.usuario=item.usuario
            this.id= item._id
            this.limpiar();

            this.comprobanteModal=1          
          },
           ocultarComprobante(item){
            this.comprobanteModal=0
          },

    selectEmbarcaciones(){
            let me = this;
            let embarcacionArray=[];
            let header={"Token":this.$store.state.token};
            let configuracion= {headers: header}
            axios.get('embarcacion/list',configuracion).then((response)=>{
              embarcacionArray=response.data;
              embarcacionArray.map(function(x){
                me.embarcaciones.push({text:x.nombre, value:x._id})
              });
            }).catch(function(error){
              console.log(error);
            })
          },

      selectAgencias(){
            let me = this;
            let agenciaArray=[];
            let header={"Token":this.$store.state.token};
            let configuracion= {headers: header}
            axios.get('agencia/list',configuracion).then((response)=>{
              agenciaArray=response.data;
              agenciaArray.map(function(x){
                me.agencias.push({text:x.agencia, value:x._id})
              });
            }).catch(function(error){
              console.log(error);
            })
          },
       selectCia(){
            let me = this;
            let companiaArray=[];
            let header={"Token":this.$store.state.token};
            let configuracion= {headers: header}
            axios.get('compania/list',configuracion).then((response)=>{
              companiaArray=response.data;
              companiaArray.map(function(x){
                me.companias.push({text:x.cia, value:x._id})
              });
            }).catch(function(error){
              console.log(error);
            })
       },

    
    editItem (item) {
        this._id=item._id
        this.cia=item.cia._id
        this.embarcacion=item.embarcacion._id
        this.agencia=item.agencia._id
        this.onhire=item.onhire
        this.programa=item.programa
        this.detalles=item.detalles
        this.tipoServicio=item.tipo_servicio
        this.urlSoporte=item.url_soporte
        this.urlCotizacion=item.url_cotizacion
        this.editedIndex=1;
        this.dialog=true
    },

   activarDesactivarMostrar(accion,item){
        this.adModal=1;
          this.adNombre=item.programa;
          this.adId=item._id;
          if(accion==1){
            this.adAccion=1
          }else if(accion==0){
            this.adAccion=0
          }else{
            this.adMmodal=0;
          }
   },
   activar(){
          let me=this;
            let header={"Token":this.$store.state.token};
            let configuracion= {headers: header}
           axios.put('confirmarServicio/activate',{'_id':this.adId},configuracion)
               .then(function(response){
                 me.adModal=0,
                 me.adAccion=0,
                 me.adNombre='',
                 me.adId='',
                 me.listar();
               }).catch(function(error){
                 console.log(error)
               });
        },

        desactivar(){
          let me=this;
            let header={"Token":this.$store.state.token};
            let configuracion= {headers: header}
           axios.put('confirmarServicio/deactivate',{'_id':this.adId},configuracion)
               .then(function(response){
                 me.adModal=0,
                 me.adAccion=0,
                 me.adNombre='',
                 me.adId='',
                 me.listar();
               }).catch(function(error){
                 console.log(error)
               });
        },
        activarDesactivarCerrar(){
          this.adModal=0;
        },

    close () {
      this.dialog = false
     
    },
    },
    
};
</script>
